var totalTime = 10;
function startTimer(seconds){
    totalTime = seconds;
    updateClock();
}
function updateClock() {
    document.getElementById('countdown').innerHTML = totalTime;
    if (totalTime == 0) {
        console.log('Final');
    } else {
        totalTime -= 1;
        setTimeout("updateClock()", 1000);
    }
}
