import React from "react";
import { useState, useEffect } from "react";
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import CountDown from './CountDown';
export default function QuestionDetails({ image, timeInSecs, prompt}) {
  const startMinutes = Math.floor(timeInSecs/60)
  const startSeconds = (timeInSecs%60)
  const [mins, setMinutes] = useState(startMinutes);
  const [secs, setSeconds] = useState(startSeconds);
  useEffect(() => {
    let sampleInterval = setInterval(() => {
      if (secs > 0) {
        setSeconds(secs - 1);
      }
      if (secs === 0) {
        if (mins === 0) {
          clearInterval(sampleInterval);
        } else {
          setMinutes(mins - 1);
          setSeconds(59);
        }
      }
    }, 1000);
    return () => {
      clearInterval(sampleInterval);
    };
  });
  return (
    <Box
      sx={{
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      <img
        src={image}
        alt="question 1"
        width="100%"
      />
      <Typography component="h4" variant="h6" textAlign='center'>
        <b>{prompt}</b>
      </Typography>
      <Typography component="span" textAlign='center'>
        {timeInSecs}
      </Typography>
      <div>
        {!(mins && secs) ? "" : (
          <p>
            {" "}
            {mins}:{secs < 10 ? `0${secs}` : secs}
          </p>
        )}
      </div>
    </Box>
  )
}
